import{_ as t,o as a,c as e,O as o}from"./chunks/framework.4afe7240.js";const u=JSON.parse('{"title":"上传图片","description":"","frontmatter":{},"headers":[{"level":2,"title":"拖拽文件","slug":"拖拽文件","link":"#拖拽文件","children":[]},{"level":2,"title":"复制粘贴","slug":"复制粘贴","link":"#复制粘贴","children":[]},{"level":2,"title":"选择文件","slug":"选择文件","link":"#选择文件","children":[]},{"level":2,"title":"图片重命名","slug":"图片重命名","link":"#图片重命名","children":[]},{"level":2,"title":"图片哈希化","slug":"图片哈希化","link":"#图片哈希化","children":[]},{"level":2,"title":"复制图片链接","slug":"复制图片链接","link":"#复制图片链接","children":[]}],"relativePath":"usage-guide/upload.md","filePath":"usage-guide/upload.md","lastUpdated":1685980904000}'),i={name:"usage-guide/upload.md"},s=o('<h1 id="上传图片" tabindex="-1">上传图片 <a class="header-anchor" href="#上传图片" aria-label="Permalink to &quot;上传图片&quot;">​</a></h1><p>在上传图片界面，你可以使用 <strong>拖拽文件</strong>、<strong>复制粘贴</strong>、<strong>选择文件</strong> 这三种方式选择你的图片到上传区域。</p><h2 id="拖拽文件" tabindex="-1">拖拽文件 <a class="header-anchor" href="#拖拽文件" aria-label="Permalink to &quot;拖拽文件&quot;">​</a></h2><p>你可以拖拽 <strong>一张</strong> 或 <strong>多张</strong> 图片到上传区域。</p><p><img src="https://cdn.staticaly.com/gh/XPoet/image-hosting@master/projects/picx/drag-upload-imgs.7h0jilr2zzc0.gif" alt="drag-upload-imgs"></p><h2 id="复制粘贴" tabindex="-1">复制粘贴 <a class="header-anchor" href="#复制粘贴" aria-label="Permalink to &quot;复制粘贴&quot;">​</a></h2><p>你可以先将一张图片复制（快捷键 <code>Ctrl+C</code> / <code>Command+C</code>）到系统剪贴板，然后在 PicX 上传界面通过按下快捷键 <code>Ctrl+V</code> / <code>Command+V</code> 将图片粘贴到上传区域。</p><p><img src="https://cdn.staticaly.com/gh/XPoet/image-hosting@master/projects/picx/copy-upload-img.2agpfbdsg2jo.gif" alt="copy-upload-img"></p><h2 id="选择文件" tabindex="-1">选择文件 <a class="header-anchor" href="#选择文件" aria-label="Permalink to &quot;选择文件&quot;">​</a></h2><p>你可以选择 <strong>一张</strong> 或 <strong>多张</strong> 图片到上传区域。</p><p><img src="https://cdn.staticaly.com/gh/XPoet/image-hosting@master/projects/picx/select-upload.5u91jqc4aaw0.gif" alt="select-upload"></p><h2 id="图片重命名" tabindex="-1">图片重命名 <a class="header-anchor" href="#图片重命名" aria-label="Permalink to &quot;图片重命名&quot;">​</a></h2><p>选择图片完成后，在上传之前，可以自由修改图片名。</p><p><img src="https://cdn.staticaly.com/gh/XPoet/image-hosting@master/projects/picx/image.4zib2n6efyg0.webp" alt="image"></p><h2 id="图片哈希化" tabindex="-1">图片哈希化 <a class="header-anchor" href="#图片哈希化" aria-label="Permalink to &quot;图片哈希化&quot;">​</a></h2><p>勾选中哈希化，会在图片名称加上一串哈希值，确保图片名的唯一性，<strong>强烈建议开启</strong>。</p><p><img src="https://cdn.staticaly.com/gh/XPoet/image-hosting@master/projects/picx/image.2f6r4nuv13ok.webp" alt="image"></p><h2 id="复制图片链接" tabindex="-1">复制图片链接 <a class="header-anchor" href="#复制图片链接" aria-label="Permalink to &quot;复制图片链接&quot;">​</a></h2><p>图片上传成功之后，会自动复制图片链接到系统剪贴板，也可以点击 “复制链接” 按钮进行复制。</p><p><img src="https://cdn.staticaly.com/gh/XPoet/image-hosting@master/projects/picx/image.6hzwo3q35jw0.webp" alt="image"></p><p><img src="https://cdn.staticaly.com/gh/XPoet/image-hosting@master/projects/picx/gif.rp29pb1htsg.gif" alt="gif"></p>',21),r=[s];function l(c,n,g,p,d,h){return a(),e("div",null,r)}const _=t(i,[["render",l]]);export{u as __pageData,_ as default};
